<?phpclass News extends CI_Controller {    function News() {        parent::__construct();        $this->load->library(array('encrypt', 'form_validation', 'session'));        $this->load->helper(array('form', 'url', 'html', 'aviocompany_url'));        $this->load->model(array('news_model', 'users_model', 'comments_model'));        init_events_page($this->session, $this->lang);    }    function add() {        $title = $this->input->post('title');        $text = $this->input->post('text');        $user = $this->session->userdata('current_user');        if ($user) {            $this->news_model->insert_news($title, $text, $user->id);        }        redirect(news_url('news'));    }    function edit() {        $news_id = $this->input->post('news_id');        $title = $this->input->post('title');        $text = $this->input->post('text');        $user = $this->session->userdata('current_user');        $this->news_model->update_news($news_id, $name,$title, $text,);        redirect(news_url('news'));    }    function delete() {        $news_id = $this->input->get('news_id');        $this->news_model->delete_news($news_id);        redirect_back($this->session);    }    private function render_news($news) {        $this->comments_model->load_comment_count($news);        $data['news'] = $news;        $this->load->view('news_view', $data);    }}