<?phpclass News extends CI_Controller {    function News() {        parent::__construct();        $this->load->library(array('encrypt', 'form_validation', 'session', 'lang'));        $this->load->helper(array('form', 'url', 'html', 'avio'));        $this->load->model(array('news_model', 'users_model'));        init_avio_page($this->session, $this->lang);    }	    function index() {          $news=$this->news_model->get_news();        $data['news'] = $news;        $this->load->view('news_view', $data);    }    function show_add(){	}    function add() {        $user_id = $this->input->post('user_id');        $title = $this->input->post('title');        $text = $this->input->post('text');        $this->news_model->insert_new($user_id, $title, $text);		$this->session->set_flashdata('info', $this->lang->line('ui_news_add'));        redirect('news');    }    function edit() {        $new_id = $this->input->post('new_id');        $user_id = $this->input->post('user_id');        $title = $this->input->post('title');        $text = $this->input->post('text');        $this->news_model->update_new($new_id, $user_id, $title, $text);		$this->session->set_flashdata('info', $this->lang->line('ui_news_edit'));        redirect('news');    }    function delete() {        $new_id = $this->input->get('new_id');        $this->news_model->delete_new($new_id);		$this->session->set_flashdata('info', $this->lang->line('ui_news_delete'));        redirect('news');    }}