<?phpclass News_model extends CI_Model {    function get_news() {        $query = $this->db->query("select * from news");        $news = $query->result();        return $news;    }    function insert_new($user_id, $title, $text) {        $created_date = date("Y-m-d H:i:s");        $this->db->insert('news', array("user_id" => $user_id, "title" => $title, "text" => $text, "created_date" => $created_date));    }    function update_new($new_id, $user_id, $title, $text) {        $this->db->update('news', array("user_id" => $user_id, "title" => $title, "text" => $text), array('id' => $new_id));    }    function delete_new($new_id) {            $this->db->delete('news', array("id" => $new_id));    }}