<?phpclass News_model extends CI_Model {    function get_news() {        $query = $this->db->query("select * from news");        $news = $query->result();        return $news;    }    function insert_new($title, $text) {        $this->db->insert('news', array("title" => $title, "text" => $text));    }    function update_new($new_id, $title, $text) {        $this->db->update('news', array("title" => $title, "text" => $text), array('id' => $new_id));    }    function delete_new($new_id) {            $this->db->delete('news', array("id" => $new_id));    }	    function get_new_list() {        $news = $this->get_news();        $new_list = array();        foreach ($news as $new){            $new_list[$new->id] = $new->title;        }        return $new_list;    }}