<table class="news_table">    <div class='Title'><h1>Creating news</h1></div>            <?=form_open('news/add')?>		<td align="left">Title:<?=form_input('title','')?></td>		<tr>		<td align="left"><?=form_textarea('text','')?></td>		</tr>		<td align="right"><?=form_submit('add_city', 'Add')?></td>		<?=form_close();?>		        <table class="Content">            <?php foreach ($news as $new): ?>            <?=form_open('news/edit')?>            <?=form_hidden('new_id', $new->id)?>			<td>Title:<?=form_input('title', $new->title)?></td>            <tr>				<td><?=form_textarea('text', $new->text)?></td>			</tr>                <td align="right">                    <?=form_submit('edit_new', 'Edit')?>                    <a href='<?='news/delete?new_id='.$new->id?>'>                    <span class="deleteIcon"><?=img('images/delete_icon.png')?></span></a>                </td>                        <?=form_close();?>        <?php endforeach; ?>        </table></table>