<?php include("header.php"); ?><h1 class="title">News</h1><a class="btn btn-primary" data-toggle="modal" href="#newNew" >Add news</a><table class="table table-bordered newsTable"><h2>Existing news</h2>    <thead>        <tr>            <td width='100px'>Name</td>			<td>Text</td>			<td>Actions</td>	    </tr>    </thead>			<?php foreach ($news as $new): ?>    <?php        $view_id = "view_".$new->id;        $edit_id = "edit_".$new->id;    ?> <?=form_open('news/edit')?>            <?=form_hidden('new_id', $new->id)?>    <tr>        <td>            <div class='view_data <?=$view_id?>'><?=$new->title?></div>            <div class='edit_data <?=$edit_id?>'><?=form_input('title', $new->title)?></div>        </td>	    <td>            <div class='view_data <?=$view_id?>'><?=$new->text?></div>            <div class='edit_data <?=$edit_id?>'><?=form_textarea('text', $new->text)?></div>        </td>        <td>            <span class='edit_data <?=$edit_id?>'>                <?=form_submit('edit_new', 'Save', "class='btn btn-primary'")?>                <a class="cancel_button"><i class="icon-remove"></i></a>            </span>            <span class='view_data <?=$view_id?>'>                <a class="edit_button" id=<?="'edit_".$new->id."'" ?>><i class="icon-edit"></i></a>                <a href='<?='news/delete?new_id='.$new->id?>'><i class="icon-trash"></i></a>            </span>        </td>    </tr>    <?=form_close();?>    <?php endforeach; ?>	</table><div class="modal" id="newNew">    <div class="modal-header">        <button class="close" data-dismiss="modal"><i class="icon-remove"></i></button>        <h3>Create news</h3>    </div>    <?=form_open('news/add', array("class" => "form-horizontal"))?>        <div class="modal-body">            <div class="control-group">                <label class="control-label" for="title">Title</label>                <div class="controls">		                    <?=form_input('title','')?>			    </div>			</div>               <div class="control-group">                <label class="control-label" for="text">Text</label>                <div class="controls">		                    <?=form_textarea('text','')?>			    </div>			</div>		</div> 			        <div class="modal-footer">            <a class="btn" id="newNewClose">Close</a>            <?=form_submit('add_new', 'Save', "class='btn btn-primary'")?>        </div>    <?=form_close();?>       </div><script type="text/javascript">    $(function() {        var newNew = $("#newNew");        newNew.modal().modal('hide');        $("#newNewClose").on('click', function() {            newNew.modal('hide');        });        $(".edit_data").hide();        $(".edit_button").on('click', function() {            var id = $(this).attr('id').replace("edit_", "");            $(".view_" + id).hide();            $(".edit_" + id).show();        });        $(".cancel_button").on('click', function() {            $(".view_data").show();            $(".edit_data").hide();        });    })</script><?php include("footer.php"); ?>